<?phpnamespace SosyalliftAIPro\Admin\Ajax;use SosyalliftAIPro\Core\Dashboard\DataProvider;defined('ABSPATH') || exit;class DashboardAjax {    public static function init(): void {        add_action('wp_ajax_sl_ai_pro_dashboard_refresh', [self::class, 'refresh']);    }    public static function refresh(): void {        check_ajax_referer('sl_ai_pro_dashboard', 'nonce');        if (!current_user_can('manage_options')) {            wp_send_json_error('unauthorized', 403);        }        wp_send_json_success(            DataProvider::get_dashboard_payload()        );    }}