<?phpnamespace SosyalliftAIPro\Admin\Repositories;defined('ABSPATH') || exit;class DashboardRepository {    public function get_overview_stats(): array {        global $wpdb;        return [            'keywords' => (int) $wpdb->get_var("SELECT COUNT(*) FROM {$wpdb->prefix}sl_ai_keywords"),            'logs'     => (int) $wpdb->get_var("SELECT COUNT(*) FROM {$wpdb->prefix}sl_ai_logs"),            'intents'  => (int) $wpdb->get_var("SELECT COUNT(*) FROM {$wpdb->prefix}sl_ai_intent"),        ];    }    public function get_latest_alerts(): array {        global $wpdb;        return $wpdb->get_results(            "SELECT message, level, created_at             FROM {$wpdb->prefix}sl_ai_logs             WHERE level IN ('warning','error')             ORDER BY created_at DESC             LIMIT 5",            ARRAY_A        );    }    public function get_latest_logs(): array {        global $wpdb;        return $wpdb->get_results(            "SELECT message, level, created_at             FROM {$wpdb->prefix}sl_ai_logs             ORDER BY created_at DESC             LIMIT 10",            ARRAY_A        );    }}