<?phpuse SosyalliftAIPro\Core\Logs\LogRepository;if (!current_user_can('manage_options')) {    wp_die('Unauthorized');}$format = $_GET['format'] ?? 'csv';$repo   = new LogRepository();$logs   = $repo->fetch();if ($format === 'json') {    header('Content-Type: application/json');    echo json_encode($logs);    exit;}header('Content-Type: text/csv');header('Content-Disposition: attachment; filename="logs.csv"');$output = fopen('php://output', 'w');fputcsv($output, array_keys($logs[0] ?? []));foreach ($logs as $log) {    fputcsv($output, $log);}fclose($output);exit;