<?phpuse SosyalliftAIPro\Core\Logs\LogRepository;$repo = new LogRepository();$logs = $repo->fetch();?><div class="wrap">    <h1>System Logs</h1>    <table class="widefat striped">        <thead>            <tr>                <th>Date</th>                <th>Type</th>                <th>Source</th>                <th>Level</th>                <th>Message</th>            </tr>        </thead>        <tbody>        <?php foreach ($logs as $log): ?>            <tr>                <td><?= esc_html($log['created_at']) ?></td>                <td><?= esc_html($log['log_type']) ?></td>                <td><?= esc_html($log['source']) ?></td>                <td><?= esc_html($log['level']) ?></td>                <td><?= esc_html($log['message']) ?></td>            </tr>        <?php endforeach; ?>        </tbody>    </table></div>