<?phpdefined('ABSPATH') || exit;if (isset($_POST['sl_ai_settings_save'])) {    check_admin_referer('sl_ai_settings_save');    update_option('sl_ai_debug_mode', isset($_POST['debug_mode']) ? 1 : 0);    update_option('sl_ai_auto_sync', isset($_POST['auto_sync']) ? 1 : 0);    echo '<div class="updated notice"><p>Ayarlar güncellendi.</p></div>';}?><div class="wrap">    <h1>Sosyallift AI – Ayarlar</h1>    <form method="post">        <?php wp_nonce_field('sl_ai_settings_save'); ?>        <table class="form-table">            <tr>                <th>Debug Modu</th>                <td>                    <input type="checkbox" name="debug_mode" value="1"                        <?= checked(1, get_option('sl_ai_debug_mode'), false) ?>>                </td>            </tr>            <tr>                <th>Otomatik Senkronizasyon</th>                <td>                    <input type="checkbox" name="auto_sync" value="1"                        <?= checked(1, get_option('sl_ai_auto_sync'), false) ?>>                </td>            </tr>        </table>        <p>            <button class="button button-primary" name="sl_ai_settings_save">                Kaydet            </button>        </p>    </form></div>